import './App.css';
import ReactDOM from 'react-dom'
import React from 'react';
import {useState, useEffect} from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { name: '',  email: '', pin: '', message: ''};
  }
  mySubmitHandler1 = (event) => {
    event.preventDefault();
    /*const response = await fetch('http://localhost:3000/submitform', {
      headers: {
        'Access-Control-Allow-Origin': '*',
      },
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state}),
    });
    const body = await response.text();*/
    ///console.log(JSON.stringify(this.state))
    fetch('http://localhost:3000/submitform', {
      /*headers: {
        'Access-Control-Allow-Origin': '*',
      },*/
      headers: {
        'Content-type': 'application/json'
      },
      method: 'POST',
      // We convert the React state to JSON and send it as the POST body
      body: JSON.stringify(this.state) }
    ).then(function(response) {
      console.log('Response received');
      console.log(response.json())
      return response.json();
    });

    alert("You are submitting " + this.state.name);
  }

  mySubmitHandler = (event) => {
    //event.preventDefault();
    fetch('http://localhost:3000/getmessages', {
    method: 'GET'
    })
    .then((response) => {
      console.log(response);
      return response.json();
  })
  .then((myJson) => {
      this.setState({
        isLoaded: true,
        posts: myJson,
      });
      console.log('testing');
      //console.log(myJson);
  });

    alert("You are submitting " + this.state.name);
  }
  myChangeHandler = (event) => {
    if (event.target.name === 'name')
      this.setState({name: event.target.value});
    else if (event.target.name === 'email')
      this.setState({email: event.target.value});
    else if (event.target.name === 'pin')
      this.setState({pin: event.target.value});
    else if (event.target.name === 'message')
      this.setState({message: event.target.value});
  }
  render() {
    return (
      <form onSubmit={this.mySubmitHandler}>
      <h1>Hello {this.state.name}</h1>
      <p>Please enter your details below</p>
      <label>
        Name:
        <input 
          type="text" 
          name="name" 
          onChange={this.myChangeHandler}
        />
      </label>
      <p></p>


      <label>
        Email:
        <input 
          type="text" 
          name="email" 
          onChange={this.myChangeHandler}
        />
      </label>
      <p></p>

      <label>
        Pin:
        <input 
          type="text" 
          name="pin" 
          onChange={this.myChangeHandler}
        />
      </label>
      <p></p>

      <label>
        Message:
        <input 
          type="text" 
          name="message" 
          onChange={this.myChangeHandler}
        />
      </label>
      <p></p>

      <input
        type='submit'
      />
      </form>
    );
  }
}

export default App;
